package gogo

import (
    "testing"
    "math"
)

func Test_myAtoi(t *testing.T) {
    tests := []string{"123", "-123", "2147483648", "  1234a6789", "    -0012a42", " asdfdfs", "", "+1   ", "-", ""}
    results := []int{123, -123, math.MaxInt32, 1234, -12, 0, 0, 1, 0, math.MaxInt32}

    for i := 0; i < len(tests); i++ {
        result := myAtoi(tests[i])
        if result != results[i] {
            t.Fatalf("expected: %v, actual: %v", results[i], result)
        }
    }
}
